        public static string morganAndString(string a, string b)
        {
            var result = "";
            var itemLower = a;
            var itemUpper = b;
            if (a.Length > b.Length)
            {
                itemLower = b;
                itemUpper = a;
            }

            var aIndex = 0;
            var bIndex = 0;
            var equalTime = 0;
            while (aIndex < a.Length && bIndex < b.Length)
            {

                if (a[aIndex] < b[bIndex])
                {
                    result += a[aIndex];
                    aIndex++;
                }

                if (a[aIndex] == b[bIndex])
                {
                    if (equalTime % 2 == 0)
                    {
                        aIndex++;
                        result += a[aIndex];
                    }
                    else
                    {
                        result += b[bIndex];
                        bIndex ++;
                    }

                    equalTime++;
                }
                else
                {
                    result += b[bIndex];
                    bIndex++;
                }
            }

            if (bIndex < b.Length)
            {
                result += b.Substring(bIndex);
            }
            else
            {
                result += a.Substring(aIndex);
            }
            return result;
        }


